import { useState, useEffect, useCallback } from "react";
import { useLocation, useNavigate, UNSAFE_NavigationContext } from "react-router-dom";
import axios from "axios";
import Navbar from "../../components/Navbar";
import { useContext } from "react";

function useBlocker(blocker, when = true) {
  const { navigator } = useContext(UNSAFE_NavigationContext);

  useEffect(() => {
    if (!when) return;

    const unblock = navigator.block((tx) => {
      const autoUnblockingTx = {
        ...tx,
        retry() {
          unblock();
          tx.retry();
        },
      };

      blocker(autoUnblockingTx);
    });

    return unblock;
  }, [navigator, blocker, when]);
}

function PlayerQuiz() {
  const location = useLocation();
  const navigate = useNavigate();
  const { categoryId, questionCount } = location.state || {};

  const [questions, setQuestions] = useState([]);
  const [currentAnswers, setCurrentAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [score, setScore] = useState(0);

  // Block back navigation using React Router blocker
  const blocker = useCallback((tx) => {
    // Confirm with user before leaving the quiz
    if (
      window.confirm(
        "Are you sure you want to leave? Your quiz progress will be lost."
      )
    ) {
      tx.retry(); // allow navigation
    }
  }, []);

  useBlocker(blocker, true);

  useEffect(() => {
    if (!categoryId || !questionCount) {
      navigate("/player/dashboard", { replace: true });
      return;
    }

    const fetchQuestions = async () => {
      try {
        setLoading(true);
        const response = await axios.get("http://localhost:5000/api/questions/quiz", {
          params: { categoryId, count: questionCount },
        });
        setQuestions(response.data);
      } catch (error) {
        setError("Failed to load questions");
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();

    // Prevent back navigation by pushing current location again (rewrite history)
    window.history.pushState(null, "", window.location.href);

    // Listen to back/forward browser button
    const onPopState = () => {
      // Push user back forward so they can't go back
      window.history.pushState(null, "", window.location.href);
      alert("Back navigation is disabled during the quiz.");
    };

    window.addEventListener("popstate", onPopState);

    // Warn before reload/close
    const onBeforeUnload = (e) => {
      e.preventDefault();
      e.returnValue = "Are you sure you want to leave the quiz?";
    };

    window.addEventListener("beforeunload", onBeforeUnload);

    return () => {
      window.removeEventListener("popstate", onPopState);
      window.removeEventListener("beforeunload", onBeforeUnload);
    };
  }, [categoryId, questionCount, navigate]);

  const handleAnswerSelect = (questionId, optionIndex) => {
    setCurrentAnswers((prev) => ({
      ...prev,
      [questionId]: optionIndex,
    }));

    const question = questions.find((q) => q._id === questionId);
    if (question.correctOption === optionIndex) {
      setScore((prev) => prev + 1);
    }
  };

  const handleSubmitQuiz = async () => {
    try {
      const token = localStorage.getItem("token");
      const answeredQuestions = Object.keys(currentAnswers).length;

      if (answeredQuestions < questions.length) {
        const confirmSubmit = window.confirm(
          `You’ve only answered ${answeredQuestions} out of ${questions.length} questions. Submit anyway?`
        );
        if (!confirmSubmit) return;
      }

      const results = questions.map((q) => ({
        questionId: q._id,
        selectedOption: currentAnswers[q._id] ?? null,
        isCorrect: currentAnswers[q._id] === q.correctOption,
      }));

      await axios.post(
        "http://localhost:5000/api/quiz/submit",
        { categoryId, results, score },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Allow navigation after submit
      navigate("/player/quiz-review", {
        state: { questions, currentAnswers, score },
      });
    } catch (error) {
      setError("Failed to submit quiz");
      console.error(error);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100">
        <Navbar />
        <div className="container mx-auto px-4 py-8 text-center text-lg">
          Loading quiz questions...
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-md p-6 max-w-3xl mx-auto">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-purple-600">Quiz in Progress</h1>
            <div className="text-lg font-semibold">
              Score: {score} / {questions.length}
            </div>
          </div>

          {error && (
            <div className="bg-red-100 text-red-700 px-4 py-3 rounded mb-4">
              {error}
            </div>
          )}

          <div className="space-y-8">
            {questions.map((question, index) => (
              <div key={question._id} className="border rounded-lg p-4">
                <h3 className="text-lg font-semibold mb-3">
                  {index + 1}. {question.text}
                </h3>
                <div className="space-y-2">
                  {question.options.map((option, idx) => {
                    const isSelected = currentAnswers[question._id] === idx;
                    return (
                      <div
                        key={idx}
                        onClick={() => handleAnswerSelect(question._id, idx)}
                        className={`p-3 rounded-md cursor-pointer border transition-colors ${
                          isSelected
                            ? "bg-blue-100 border-blue-400"
                            : "hover:bg-gray-100 border-gray-300"
                        }`}
                      >
                        <div className="flex items-start">
                          <span className="w-6">{String.fromCharCode(65 + idx)}.</span>
                          <span className="ml-2">{option}</span>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            ))}
          </div>

          <div className="mt-8 flex justify-end">
            <button
              onClick={handleSubmitQuiz}
              className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-6 rounded"
            >
              Submit Quiz
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PlayerQuiz;
