import { useState, useEffect, useRef } from 'react'
import {
  Link,
  useLocation,
  useNavigate,
  useParams,
  useSearchParams,
} from 'react-router-dom'
import axios from 'axios'
import { useAuth } from '../../contexts/AuthContext'
import { toast } from 'react-toastify'

// ✅ Internal Navbar Component (with disable logic)
function Navbar({ disableNav }) {
  const { user, logout } = useAuth()
  const navigate = useNavigate()

  const handleLogout = () => {
    logout()
    navigate('/login')
  }

  const handleNavClick = (e, to) => {
    if (disableNav) {
      e.preventDefault()
      alert('You cannot navigate away during the quiz.')
    } else {
      navigate(to)
    }
  }

  return (
    <nav className="bg-purple-700 text-white shadow-md">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center py-4">
          <div className="flex items-center">
            <Link
              to={
                user?.role === 'admin'
                  ? '/admin/dashboard'
                  : '/player/dashboard'
              }
              onClick={(e) =>
                handleNavClick(
                  e,
                  user?.role === 'admin'
                    ? '/admin/dashboard'
                    : '/player/dashboard'
                )
              }
              className="text-xl font-bold"
            >
              Quiz Game
            </Link>
          </div>

          {user && (
            <div className="flex items-center space-x-6">
              {user.role === 'player' ? (
                <>
                  <Link
                    to="/player/dashboard"
                    onClick={(e) => handleNavClick(e, '/player/dashboard')}
                    className="hover:text-purple-200"
                  >
                    Dashboard
                  </Link>
                  <Link
                    to="/player/history"
                    onClick={(e) => handleNavClick(e, '/player/history')}
                    className="hover:text-purple-200"
                  >
                    My History
                  </Link>
                </>
              ) : (
                <>
                  <Link
                    to="/admin/dashboard"
                    onClick={(e) => handleNavClick(e, '/admin/dashboard')}
                    className="hover:text-purple-200"
                  >
                    Dashboard
                  </Link>
                  <Link
                    to="/admin/questions"
                    onClick={(e) => handleNavClick(e, '/admin/questions')}
                    className="hover:text-purple-200"
                  >
                    Questions
                  </Link>
                  <Link
                    to="/admin/leaderboard"
                    onClick={(e) => handleNavClick(e, '/admin/leaderboard')}
                    className="hover:text-purple-200"
                  >
                    Leaderboard
                  </Link>
                  <Link
                    to="/admin/players"
                    onClick={(e) => handleNavClick(e, '/admin/players')}
                    className="hover:text-purple-200"
                  >
                    Players
                  </Link>
                </>
              )}

              <div className="flex items-center space-x-2">
                <span className="text-sm">{user.name}</span>
                <button
                  onClick={handleLogout}
                  className="bg-purple-800 hover:bg-purple-900 text-white text-sm py-1 px-3 rounded"
                >
                  Logout
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </nav>
  )
}

function Quiz() {
  const { categoryId } = useParams()
  const [searchParams] = useSearchParams()
  const difficulty = searchParams.get('difficulty') || 'medium'
  const navigate = useNavigate()
  const mounted = useRef(true)

  const [questions, setQuestions] = useState([])
  const [currentIndex, setCurrentIndex] = useState(0)
  const [userAnswers, setUserAnswers] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState('')
  const [quizCompleted, setQuizCompleted] = useState(false)
  const [timer, setTimer] = useState(30)
  const [score, setScore] = useState(0)
  const [stats, setStats] = useState({
    easy: { correct: 0, total: 0 },
    medium: { correct: 0, total: 0 },
    hard: { correct: 0, total: 0 },
  })

  useEffect(() => {
    // Cleanup function to prevent state updates after unmount
    return () => {
      mounted.current = false
    }
  }, [])

  useEffect(() => {
    if (!categoryId) {
      navigate('/player/dashboard')
      return
    }

    const fetchData = async () => {
      try {
        setLoading(true)
        const response = await axios.post(`/api/quiz/questions`, {
          categoryId,
          difficulty,
          count: 10,
        })

        if (!mounted.current) return

        if (!response.data || response.data.length === 0) {
          throw new Error(
            'No questions available for this category and difficulty'
          )
        }

        setQuestions(response.data)
        setError('')
      } catch (err) {
        if (!mounted.current) return
        const message = err.response?.data?.message || err.message
        setError(message)
        toast.error(message)
      } finally {
        if (mounted.current) {
          setLoading(false)
        }
      }
    }

    fetchData()
  }, [categoryId, difficulty, navigate])

  useEffect(() => {
    if (loading || quizCompleted) return

    const countdown = setInterval(() => {
      if (!mounted.current) return
      setTimer((prev) => {
        if (prev <= 1) {
          handleNextQuestion()
          return 30
        }
        return prev - 1
      })
    }, 1000)

    return () => {
      clearInterval(countdown)
    }
  }, [loading, quizCompleted, currentIndex])

  const calculatePoints = (difficulty, isCorrect) => {
    if (!isCorrect) return 0
    switch (difficulty) {
      case 'easy':
        return 1
      case 'medium':
        return 2
      case 'hard':
        return 3
      default:
        return 1
    }
  }

  const handleAnswer = (selectedOption) => {
    const currentQuestion = questions[currentIndex]
    const isCorrect = selectedOption === currentQuestion.correctOption
    const points = calculatePoints(currentQuestion.difficulty, isCorrect)

    // Update score and stats
    setScore((prev) => prev + points)
    setStats((prev) => ({
      ...prev,
      [currentQuestion.difficulty]: {
        correct: prev[currentQuestion.difficulty].correct + (isCorrect ? 1 : 0),
        total: prev[currentQuestion.difficulty].total + 1,
      },
    }))

    // Record answer
    setUserAnswers([
      ...userAnswers,
      {
        questionId: currentQuestion._id,
        selectedOption,
        isCorrect,
        points,
        difficulty: currentQuestion.difficulty,
      },
    ])

    handleNextQuestion()
  }

  const handleNextQuestion = () => {
    if (currentIndex < questions.length - 1) {
      setCurrentIndex(currentIndex + 1)
      setTimer(30)
    } else {
      completeQuiz()
    }
  }

  const completeQuiz = async () => {
    try {
      await axios.post('/api/quiz/submit', {
        categoryId,
        answers: userAnswers,
        score,
        stats,
      })
      setQuizCompleted(true)
    } catch (err) {
      toast.error('Error submitting quiz results')
    }
  }

  const getDifficultyColor = (difficulty) => {
    const colors = {
      easy: 'bg-green-100 text-green-800 border-green-200',
      medium: 'bg-yellow-100 text-yellow-800 border-yellow-200',
      hard: 'bg-red-100 text-red-800 border-red-200',
    }
    return colors[difficulty] || 'bg-gray-100 text-gray-800 border-gray-200'
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-xl">Loading quiz...</div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-red-500 text-xl">{error}</div>
      </div>
    )
  }

  if (quizCompleted) {
    return (
      <div className="container mx-auto p-4">
        <div className="bg-white rounded-lg shadow-lg p-6 max-w-2xl mx-auto">
          <h2 className="text-2xl font-bold mb-6">Quiz Completed!</h2>

          <div className="mb-6">
            <div className="text-xl mb-2">Final Score: {score} points</div>
            <div className="text-gray-600">
              Total Questions: {questions.length}
            </div>
          </div>

          <div className="mb-6">
            <h3 className="font-semibold mb-3">Performance by Difficulty:</h3>
            {Object.entries(stats).map(
              ([difficulty, { correct, total }]) =>
                total > 0 && (
                  <div key={difficulty} className="mb-2">
                    <div
                      className={`inline-block px-2 py-1 rounded-full text-sm ${getDifficultyColor(
                        difficulty
                      )} mb-1`}
                    >
                      {difficulty}
                    </div>
                    <div className="ml-2 inline-block">
                      {correct} / {total} correct (
                      {Math.round((correct / total) * 100)}%)
                    </div>
                  </div>
                )
            )}
          </div>

          <div className="mb-6">
            <h3 className="font-semibold mb-3">Points Breakdown:</h3>
            <div className="grid grid-cols-3 gap-4 text-center">
              <div className="p-2 bg-green-50 rounded">
                <div className="font-medium">Easy</div>
                <div className="text-sm text-gray-600">1 point each</div>
              </div>
              <div className="p-2 bg-yellow-50 rounded">
                <div className="font-medium">Medium</div>
                <div className="text-sm text-gray-600">2 points each</div>
              </div>
              <div className="p-2 bg-red-50 rounded">
                <div className="font-medium">Hard</div>
                <div className="text-sm text-gray-600">3 points each</div>
              </div>
            </div>
          </div>

          <button
            onClick={() => navigate('/player/dashboard')}
            className="w-full bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Return to Dashboard
          </button>
        </div>
      </div>
    )
  }

  const currentQuestion = questions[currentIndex]

  return (
    <div className="container mx-auto p-4">
      <div className="bg-white rounded-lg shadow-lg p-6 max-w-2xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <div className="text-lg">
            Question {currentIndex + 1} of {questions.length}
          </div>
          <div className="flex items-center space-x-4">
            <span
              className={`px-3 py-1 rounded-full text-sm border ${getDifficultyColor(
                currentQuestion.difficulty
              )}`}
            >
              {currentQuestion.difficulty}
            </span>
            <div className="text-lg font-medium">{timer}s</div>
          </div>
        </div>

        <div className="mb-8">
          <h2 className="text-xl mb-6">{currentQuestion.text}</h2>
          <div className="space-y-3">
            {currentQuestion.options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswer(index)}
                className="w-full text-left p-4 rounded border hover:bg-blue-50 transition-colors"
              >
                {option}
              </button>
            ))}
          </div>
        </div>

        <div className="flex justify-between items-center text-gray-600">
          <div>Current Score: {score} points</div>
          <div>
            Points:{' '}
            {currentQuestion.difficulty === 'easy'
              ? '1'
              : currentQuestion.difficulty === 'medium'
              ? '2'
              : '3'}{' '}
            pts
          </div>
        </div>
      </div>
    </div>
  )
}

export default Quiz
