import { useState, useEffect, useRef } from 'react'
import { Link, useLocation, useNavigate, useParams } from 'react-router-dom'
import axios from 'axios'
import { useAuth } from '../../contexts/AuthContext'

// ✅ Internal Navbar Component (with disable logic)
function Navbar({ disableNav }) {
  const { user, logout } = useAuth()
  const navigate = useNavigate()

  const handleLogout = () => {
    logout()
    navigate('/login')
  }

  const handleNavClick = (e, to) => {
    if (disableNav) {
      e.preventDefault()
      alert('You cannot navigate away during the quiz.')
    } else {
      navigate(to)
    }
  }

  return (
    <nav className="bg-purple-700 text-white shadow-md">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center py-4">
          <div className="flex items-center">
            <Link
              to={
                user?.role === 'admin'
                  ? '/admin/dashboard'
                  : '/player/dashboard'
              }
              onClick={(e) =>
                handleNavClick(
                  e,
                  user?.role === 'admin'
                    ? '/admin/dashboard'
                    : '/player/dashboard'
                )
              }
              className="text-xl font-bold"
            >
              Quiz Game
            </Link>
          </div>

          {user && (
            <div className="flex items-center space-x-6">
              {user.role === 'player' ? (
                <>
                  <Link
                    to="/player/dashboard"
                    onClick={(e) => handleNavClick(e, '/player/dashboard')}
                    className="hover:text-purple-200"
                  >
                    Dashboard
                  </Link>
                  <Link
                    to="/player/history"
                    onClick={(e) => handleNavClick(e, '/player/history')}
                    className="hover:text-purple-200"
                  >
                    My History
                  </Link>
                </>
              ) : (
                <>
                  <Link
                    to="/admin/dashboard"
                    onClick={(e) => handleNavClick(e, '/admin/dashboard')}
                    className="hover:text-purple-200"
                  >
                    Dashboard
                  </Link>
                  <Link
                    to="/admin/questions"
                    onClick={(e) => handleNavClick(e, '/admin/questions')}
                    className="hover:text-purple-200"
                  >
                    Questions
                  </Link>
                  <Link
                    to="/admin/leaderboard"
                    onClick={(e) => handleNavClick(e, '/admin/leaderboard')}
                    className="hover:text-purple-200"
                  >
                    Leaderboard
                  </Link>
                  <Link
                    to="/admin/players"
                    onClick={(e) => handleNavClick(e, '/admin/players')}
                    className="hover:text-purple-200"
                  >
                    Players
                  </Link>
                </>
              )}

              <div className="flex items-center space-x-2">
                <span className="text-sm">{user.name}</span>
                <button
                  onClick={handleLogout}
                  className="bg-purple-800 hover:bg-purple-900 text-white text-sm py-1 px-3 rounded"
                >
                  Logout
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </nav>
  )
}

function Quiz() {
  const { categoryId } = useParams()
  const navigate = useNavigate()
  const [questions, setQuestions] = useState([])
  const [currentIndex, setCurrentIndex] = useState(0)
  const [userAnswers, setUserAnswers] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState('')
  const [quizCompleted, setQuizCompleted] = useState(false)
  const [timer, setTimer] = useState(30)
  const [score, setScore] = useState(0)

  useEffect(() => {
    fetchQuestions()
  }, [categoryId])

  useEffect(() => {
    if (loading || quizCompleted) return

    const countdown = setInterval(() => {
      setTimer((prev) => {
        if (prev <= 1) {
          handleNextQuestion()
          return 30
        }
        return prev - 1
      })
    }, 1000)

    return () => clearInterval(countdown)
  }, [loading, quizCompleted, currentIndex])

  const fetchQuestions = async () => {
    try {
      // Get user's performance history
      const performanceResponse = await axios.get('/api/player/performance')
      const userProfile = {
        recentPerformance: performanceResponse.data,
      }

      // Fetch questions with adaptive difficulty
      const response = await axios.post(`/api/quiz/${categoryId}/questions`, {
        count: 10,
        userProfile,
      })

      setQuestions(response.data)
      setLoading(false)
    } catch (err) {
      setError('Error loading quiz questions')
      setLoading(false)
    }
  }

  const handleAnswer = (selectedOption) => {
    const currentQuestion = questions[currentIndex]
    const isCorrect = selectedOption === currentQuestion.correctOption

    // Calculate points based on difficulty
    let points = 0
    if (isCorrect) {
      switch (currentQuestion.difficulty) {
        case 'easy':
          points = 1
          break
        case 'medium':
          points = 2
          break
        case 'hard':
          points = 3
          break
        default:
          points = 1
      }
    }

    setScore((prev) => prev + points)
    setUserAnswers([
      ...userAnswers,
      {
        questionId: currentQuestion._id,
        selectedOption,
        isCorrect,
        points,
      },
    ])

    handleNextQuestion()
  }

  const handleNextQuestion = () => {
    if (currentIndex < questions.length - 1) {
      setCurrentIndex(currentIndex + 1)
      setTimer(30)
    } else {
      completeQuiz()
    }
  }

  const completeQuiz = async () => {
    try {
      await axios.post('/api/quiz/submit', {
        categoryId,
        answers: userAnswers,
        score,
      })
      setQuizCompleted(true)
    } catch (err) {
      setError('Error submitting quiz results')
    }
  }

  const getDifficultyColor = (difficulty) => {
    const colors = {
      easy: 'bg-green-100 text-green-800',
      medium: 'bg-yellow-100 text-yellow-800',
      hard: 'bg-red-100 text-red-800',
    }
    return colors[difficulty] || 'bg-gray-100 text-gray-800'
  }

  if (loading) {
    return <div className="text-center p-4">Loading quiz...</div>
  }

  if (error) {
    return <div className="text-red-500 text-center p-4">{error}</div>
  }

  if (quizCompleted) {
    return (
      <div className="container mx-auto p-4">
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-2xl font-bold mb-4">Quiz Completed!</h2>
          <p className="text-xl mb-4">Your Score: {score} points</p>
          <div className="mb-4">
            <h3 className="font-semibold mb-2">Performance Summary:</h3>
            <p>
              Correct Answers: {userAnswers.filter((a) => a.isCorrect).length}
            </p>
            <p>Total Questions: {questions.length}</p>
          </div>
          <button
            onClick={() => navigate('/player/dashboard')}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Return to Dashboard
          </button>
        </div>
      </div>
    )
  }

  const currentQuestion = questions[currentIndex]

  return (
    <div className="container mx-auto p-4">
      <div className="bg-white rounded-lg shadow p-6">
        <div className="flex justify-between items-center mb-4">
          <div className="text-lg font-semibold">
            Question {currentIndex + 1} of {questions.length}
          </div>
          <div className="flex items-center">
            <span
              className={`px-2 py-1 rounded text-sm mr-4 ${getDifficultyColor(
                currentQuestion.difficulty
              )}`}
            >
              {currentQuestion.difficulty}
            </span>
            <div className="text-lg font-semibold">Time: {timer}s</div>
          </div>
        </div>

        <div className="mb-6">
          <h2 className="text-xl mb-4">{currentQuestion.text}</h2>
          <div className="space-y-2">
            {currentQuestion.options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswer(index)}
                className="w-full text-left p-3 rounded border hover:bg-blue-50 transition-colors"
              >
                {option}
              </button>
            ))}
          </div>
        </div>

        <div className="text-right">
          <span className="text-gray-600">Score: {score} points</span>
        </div>
      </div>
    </div>
  )
}

export default Quiz
